const cartReducer = (state = initState, action) => {
  if (action.type === ADD_TO_CART) {
    let product = action.product;
    product["quantity"] = action.quantity;
    let cartArray = [];
    let cartStorage = localStorage.getItem(process.env.CART_COOKIE);

    if (cartStorage) {
      cartArray = decryptData(cartStorage);
    }
    var index = cartArray.findIndex((item) => item.id === product.id);
    if (index > -1) {
      cartArray[index]["quantity"] += action.quantity;
    } else {
      cartArray.push(product);
    }

    let encryptedData = encryptData(JSON.stringify(cartArray));

    localStorage.setItem(process.env.CART_COOKIE, encryptedData);

    return {
      ...state,
      addedItems: cartArray,
      total: 0,
    };

    // let addedItem = state.products.find(item => item.id === action.id)
    // //check if the action id exists in the addedItems
    // let existed_item= state.addedItems.find(item=> action.id === item.id)
    // if(existed_item){
    //     addedItem.quantity += 1
    //     return {
    //         ...state,
    //         total: state.total + addedItem.price
    //     }
    // } else {
    //     addedItem.quantity = 1;
    //     //calculating the total
    //     let newTotal = state.total + addedItem.price

    //     return {
    //         ...state,
    //         addedItems: [...state.addedItems, addedItem],
    //         total : newTotal
    //     }
  }

  if (action.type === ADD_TO_CART_ADDONS) {
    let cartArray = [];
    let products = action.products;
    let cartStorage = localStorage.getItem(process.env.CART_COOKIE);
    if (cartStorage) {
      cartArray = decryptData(cartStorage);
    }
    for (let i = 0; i < products.length; i++) {
      var index = cartArray.findIndex((item) => item.id === products[i].id);

      //if addond qty = 1 / if main = action qty
      if (products[i].type == "addon") {
        products[i]["quantity"] = 1;
      } else if (products[i].type == "main") {
        products[i]["quantity"] = action.quantity;
      }

      //if item is already exist
      if (index > -1) {
        if (products[i].type == "addon") {
          cartArray[index]["quantity"] += 1;
        } else if (products[i].type == "main") {
          cartArray[index]["quantity"] += action.quantity;
        }
      } else {
        cartArray.push(products[i]);
      }
    }

    let encryptedData = encryptData(JSON.stringify(cartArray));

    localStorage.setItem(process.env.CART_COOKIE, encryptedData);

    return {
      ...state,
      addedItems: cartArray,
      total: 0,
    };
  }

  if (action.type === ADD_TO_COMPARE) {
    let addedItemToCompare = state.products.find(
      (item) => item.id === action.id
    );

    addedItemToCompare.quantity = 1;

    return {
      ...state,
      addedItemsToCompare: [...state.addedItemsToCompare, addedItemToCompare],
    };
  }

  if (action.type === ADD_QUANTITY_WITH_NUMBER) {
    let addedItem = state.products.find((item) => item.id === action.id);
    //check if the action id exists in the addedItems
    let existed_item = state.addedItems.find((item) => action.id === item.id);
    if (existed_item) {
      addedItem.quantity += action.qty;
      return {
        ...state,
        total: state.total + addedItem.price * action.qty,
      };
    } else {
      addedItem.quantity = action.qty;
      //calculating the total
      let newTotal = state.total + addedItem.price * action.qty;

      return {
        ...state,
        addedItems: [...state.addedItems, addedItem],
        total: newTotal,
      };
    }
  }

  if (action.type === REMOVE_ITEM) {
    let cartStorage = localStorage.getItem(process.env.CART_COOKIE);
    if (cartStorage) {
      cartStorage = decryptData(cartStorage);
      let itemToRemove = cartStorage.find((item) => action.id === item.id);
      let new_items = cartStorage.filter((item) => action.id !== item.id);

      let encryptedData = encryptData(JSON.stringify(new_items));

      localStorage.setItem(process.env.CART_COOKIE, encryptedData);

      //calculating the total
      let newTotal =
        state.total - itemToRemove.discount_price * itemToRemove.quantity;

      return {
        ...state,
        addedItems: new_items,
        total: newTotal,
      };
    }
  }

  if (action.type === REMOVE_ITEM_FROM_COMPARE) {
    let new_items = state.addedItemsToCompare.filter(
      (item) => action.id !== item.id
    );

    return {
      ...state,
      addedItemsToCompare: new_items,
    };
  }

  if (action.type === ADD_QUANTITY) {
    let addedItem = state.products.find((item) => item.id === action.id);
    addedItem.quantity += 1;
    let newTotal = state.total + addedItem.price;
    return {
      ...state,
      total: newTotal,
    };
  }

  if (action.type === SUB_QUANTITY) {
    let addedItem = state.products.find((item) => item.id === action.id);
    //if the qt == 0 then it should be removed
    if (addedItem.quantity === 1) {
      let new_items = state.addedItems.filter((item) => item.id !== action.id);
      let newTotal = state.total - addedItem.price;
      return {
        ...state,
        addedItems: new_items,
        total: newTotal,
      };
    } else {
      addedItem.quantity -= 1;
      let newTotal = state.total - addedItem.price;
      return {
        ...state,
        total: newTotal,
      };
    }
  }

  if (action.type === ADD_SHIPPING) {
    return {
      ...state,
      shipping: (state.shipping += 6),
    };
  }

  if (action.type === "SUB_SHIPPING") {
    return {
      ...state,
      shipping: (state.shipping -= 6),
    };
  }

  if (action.type === UPDATE_CART) {
    let cartStorage = localStorage.getItem(process.env.CART_COOKIE);
    if (cartStorage) {
      cartStorage = decryptData(cartStorage);

      let encryptedData = encryptData(JSON.stringify(cartStorage));

      localStorage.setItem(process.env.CART_COOKIE, encryptedData);

      //calculating the total
      let newTotal =
        state.total - cartStorage.discount_price * cartStorage.quantity;

      return {
        ...state,
        addedItems: cartStorage,
        total: newTotal,
      };
    }
  }

  if (action.type === RESET_CART) {
    return {
      ...state,
      addedItems: [],
      total: 0,
      shipping: 0,
    };
  } else {
    return state;
  }
};

export const initStore = (initialState = initState) => {
  return createStore(
    cartReducer,
    initialState,
    composeWithDevTools(applyMiddleware(thunkMiddleware))
  );
};
