import { useState, useEffect, useCallback } from "react";
import Router from "next/router";
import {
  decryptInfoData,
  encryptInfoData,
} from "../../store/actions/infoActions";

function useForm(
  stateSchema,
  validationSchema = {},
  callback,
  handleStateChange
) {
  const [state, setState] = useState(stateSchema);
  const [disable, setDisable] = useState(true);
  const [isDirty, setIsDirty] = useState(false);

  useEffect(() => {
    let decryptedData = decryptInfoData();
    if (decryptedData) {
      // if (
      //   !decryptedData.delivery_date.value ||
      //   !decryptedData.delivery_time.value
      // ) {
      //   alert("Please input delivery date and time");
      //   window.location.href = "/cart";
      // } else {
      //   setState(decryptedData);
      // }
      setState(decryptedData);
    }
  }, []);

  useEffect(() => {
    setDisable(true);
  }, []);

  useEffect(() => {
    if (isDirty) {
      setDisable(validateState());
    }
  }, [state, isDirty]);

  // Used to disable submit button if there's an error in state
  // or the required field in state has no value.
  // Wrapped in useCallback to cached the function to avoid instensive memory leaked
  // in every re-render in component
  const validateState = useCallback(() => {
    const hasErrorInState = Object.keys(validationSchema).some((key) => {
      const isInputFieldRequired = validationSchema[key].required;
      // Get state value
      const stateValue = state[key].value;
      const stateError = state[key].error;

      return (isInputFieldRequired && !stateValue) || stateError;
    });

    return hasErrorInState;
  }, [state, validationSchema]);

  function handleOnChange(event) {
    setIsDirty(true);

    const name = event.target.name;
    const value = event.target.value;

    let error = "";
    if (validationSchema[name].required) {
      if (!value) {
        error = "This is required field.";
      }
    }

    if (
      validationSchema[name].validator !== null &&
      typeof validationSchema[name].validator === "object"
    ) {
      if (validationSchema[name].validator.regEx) {
        if (value && !validationSchema[name].validator.regEx.test(value)) {
          error = validationSchema[name].validator.error;
        }
      }
    }

    setState((prevState) => ({
      ...prevState,
      [name]: { value, error },
    }));

    // handleStateChange(event);
  }

  function handleOnSubmit(event) {
    event.preventDefault();

    // Making sure that validateState returns false
    // Before calling the submit callback function
    if (!validateState()) {
      // callback();
    }
  }

  return { state, disable, handleOnChange, handleOnSubmit, setState };
}

export default useForm;
