              style={{
                paddingTop: 0,
                marginTop: "20px",
                marginLeft: "0.5rem",
                marginRight: "0.5rem",
              }}
            >
              <div>
                <p>
                  <input
                    type="radio"
                    id="direct-bank-transfer"
                    name="radio-button"
                    onChange={() => handleChange("bank")}
                  />
                  <label htmlFor="direct-bank-transfer">
                    Direct Bank Transfer
                  </label>
                  {selectedPayment == "bank" ? (
                    <>
                      <hr />
                      <img
                        src={require("../../images/logos/banks2.jpg")}
                        alt="image"
                        height="150"
                      />
                      <p style={{ paddingTop: "20px" }}>
                        Once the order is completed, you can settle the amount
                        into our BPI account (via deposit or bank transfer)
                      </p>
                      <div>
                        <div
                          className="d-flex justify-content-between"
                          style={{ maxWidth: "300px" }}
                        >
                          <h6>Bank:</h6>
                          <h6>BPI</h6>
                        </div>
                        <div
                          className="d-flex justify-content-between"
                          style={{ maxWidth: "300px" }}
                        >
                          <h6>Account Name:</h6>
                          <h6>I-Wheels</h6>
                        </div>
                        <div
                          className="d-flex justify-content-between"
                          style={{ maxWidth: "300px" }}
                        >
                          <h6>Account Number:</h6>
                          <h6>3039022566</h6>
                        </div>
                      </div>
                      <p style={{ color: "red", paddingTop: "20px" }}>
                        <b>Note:</b> Please send proof of payment (screenshot,
                        printscreen) to{" "}
                        <span style={{ color: "#33bd33" }}>
                          i-Wheels26@gmail.com
                        </span>{" "}
                        or send a message to our{" "}
                        <a
                          className="d-inline"
                          href="https://www.facebook.com/i-Wheelsph-127061742122440/"
                        >
                          Facebook Page
                        </a>
                        .
                      </p>
                      <br />
                      <br />
                      {!isLoading ? (
                        <button
                          onClick={() => handleSubmit("Bank - BPI")}
                          className="btn btn-light mb-3"
                        >
                          Confirm Order
                        </button>
                      ) : (
                        <div className="lds-ring">
                          <div></div>
                          <div></div>
                          <div></div>
                          <div></div>
                        </div>
                      )}

                      <hr />
                    </>
                  ) : null}
                </p>
              </div>
              <div>
                <p>
                  <input
                    type="radio"
                    id="paypal"
                    name="radio-button"
                    onChange={() => handleChange("paypal")}
                  />
                  <label htmlFor="paypal">PayPal</label>
                  {selectedPayment == "paypal" ? (
                    <>
                      <hr />
                      <img
                        src={require("../../images/logos/paypal.jpg")}
                        alt="image"
                        height="70"
                      />
                      <p style={{ paddingTop: "20px" }}>
                        After clicking "Paypal checkout, a modal window will
                        pop up to PayPal to complete your purchase securely.
                      </p>
                      <br />
                      {!isLoading ? (
                        // <button
                        //   onClick={() => handleSubmit("Paypal")}
                        //   className="btn btn-light mb-3"
                        // >
                        //   Confirm Order
                        // </button>
                        <Paypal handleSubmit={handleSubmit} />
                      ) : (
                        <div className="lds-ring">
                          <div></div>
                          <div></div>
                          <div></div>
                          <div></div>
                        </div>
                      )}
                      <hr />
                    </>
                  ) : null}
                </p>
              </div>
              <div>
                <p>
                  <input
                    type="radio"
                    id="credit_card"
                    name="radio-button"
                    onChange={() => handleChange("credit_card")}
                  />
                  <label htmlFor="credit_card">Credit Card</label>
                  {selectedPayment == "credit_card" ? (
                    <>
                      <hr />
                      <img
                        src={require("../../images/logos/credit_card.jpg")}
                        alt="image"
                        height="70"
                      />
                      <p style={{ paddingTop: "20px" }}>
                        After clicking "Paypal checkout, a modal window will
                        pop up to PayPal. <br />
                        <b>Scroll down</b> then select{" "}
                        <b>Pay with Debit or Credit Card</b>
                      </p>
                      <br />
                      {!isLoading ? (
                        // <button
                        //   onClick={() => handleSubmit("Paypal")}
                        //   className="btn btn-light mb-3"
                        // >
                        //   Confirm Order
                        // </button>
                        <Paypal handleSubmit={handleSubmit} />
                      ) : (
                        <div className="lds-ring">
                          <div></div>
                          <div></div>
                          <div></div>
                          <div></div>
                        </div>
                      )}
                      <hr />
                    </>
                  ) : null}
                </p>
              </div>
              {isCodOn ? (
                <div>
                  <p>
                    <input
                      type="radio"
                      id="cash-on-delivery"
                      name="radio-button"
                      onChange={() => handleChange("cod")}
                    />
                    <label htmlFor="cash-on-delivery">Cash on Delivery</label>
                    {selectedPayment == "cod" ? (
                      <>
                        <hr />
                        <img
                          src={require("../../images/logos/cod.jpg")}
                          alt="image"
                          height="100"
                        />
                        <p style={{ paddingTop: "10px" }}>
                          When using our Cash on Delivery service, payment is
                          given to our delivery rider upon receipt of item.
                        </p>
                        <br />
                        {!isLoading ? (
                          <button
                            onClick={() => handleSubmit("COD")}
                            className="btn btn-light mb-3"
                          >
                            Confirm Order
                          </button>
                        ) : (
                          <div className="lds-ring">
                            <div></div>
                            <div></div>
                            <div></div>
                            <div></div>
                          </div>
                        )}
                        <hr />
                      </>
                    ) : null}
                  </p>
                </div>
              ) : (
                <div>
                  <p style={{ fontWeight: "bold" }}>
                    <label htmlFor="cash-on-delivery">
                      Cash on Delivery -{" "}
                      <span style={{ color: "red" }}>
                        ONLY AVAILABLE IN MAKATI AND BATANGAS
                      </span>
                      <br />
                      <span style={{ fontSize: "0.7rem" }}>
                        (Feel free to contact us if you have questions)
                      </span>
                    </label>
                    {selectedPayment == "cod" ? (
                      <>
                        <hr />
                        <img
                          src={require("../../images/logos/cod.jpg")}
                          alt="image"
                          height="100"
                        />
                        <p style={{ paddingTop: "10px" }}>
                          When using our Cash on Delivery service, payment is
                          given to our delivery rider upon receipt of item.
                        </p>
                        <br />
                        {!isLoading ? (
                          <button
                            onClick={() => handleSubmit("COD")}
                            className="btn btn-light mb-3"
                          >
                            Confirm Order
                          </button>
                        ) : (
                          <div className="lds-ring">
                            <div></div>
                            <div></div>
                            <div></div>
                            <div></div>
                          </div>
                        )}
                        <hr />
                      </>
                    ) : null}
                  </p>
                </div>
              )}

              <div>
                <p>
                  <input
                    type="radio"
                    id="gcash"
                    name="radio-button"
                    onChange={() => handleChange("gcash")}
                  />

                  <label htmlFor="gcash">GCash</label>
                  {selectedPayment == "gcash" ? (
                    <>
                      <hr />
                      <img
                        src={require("../../images/logos/gcash.png")}
                        alt="image"
                        height="70"
                      />
                      <p style={{ paddingTop: "20px" }}>
                        Once the order is completed, you can settle the amount
                        into our Gcash account (via deposit or bank transfer)
                      </p>
                      <div>
                        <div
                          className="d-flex justify-content-between"
                          style={{ maxWidth: "300px" }}
                        >
                          <h6>E-Wallet:</h6>
                          <h6>GCash</h6>
                        </div>
                        <div
                          className="d-flex justify-content-between"
                          style={{ maxWidth: "300px" }}
                        >
                          <h6>Account Name:</h6>
                          <h6>Marxs Joshua</h6>
                        </div>
                        <div
                          className="d-flex justify-content-between"
                          style={{ maxWidth: "300px" }}
                        >
                          <h6>Mobile Number:</h6>
                          <h6>09214430131</h6>
                        </div>
                      </div>
                      <p style={{ color: "red", paddingTop: "20px" }}>
                        <b>Note:</b> Please send proof of payment (screenshot,
                        printscreen) to{" "}
                        <span style={{ color: "#33bd33" }}>
                          i-Wheels26@gmail.com
                        </span>{" "}
                        or send a message to our{" "}
                        <a
                          className="d-inline"
                          href="https://www.facebook.com/i-Wheelsph-127061742122440/"
                        >
                          Facebook Page
                        </a>
                        .
                      </p>
                      <br />
                      {!isLoading ? (
                        <button
                          onClick={() => handleSubmit("GCash")}
                          className="btn btn-light mb-3"
                        >
                          Confirm Order
                        </button>
                      ) : (
                        <div className="lds-ring">
                          <div></div>
                          <div></div>
                          <div></div>
                          <div></div>
                        </div>
                      )}
                      <hr />
                    </>
                  ) : null}
                </p>
              </div>
              <div>
                <p>
                  <input
                    type="radio"
                    id="paymaya"
                    name="radio-button"
                    onChange={() => handleChange("paymaya")}
                  />
                  <label htmlFor="paymaya">Paymaya</label>
                  {selectedPayment == "paymaya" ? (
                    <>
                      <hr />
                      <img
                        src={require("../../images/logos/paymaya.jpg")}
                        alt="image"
                        height="50"
                      />
                      <p style={{ paddingTop: "20px" }}>
                        Once the order is completed, you can settle the amount
                        into our Paymaya account (via deposit or bank transfer)
                      </p>
                      <div>
                        <div
                          className="d-flex justify-content-between"
                          style={{ maxWidth: "300px" }}
                        >
                          <h6>E-Wallet:</h6>
                          <h6>Paymaya</h6>
                        </div>
                        <div
                          className="d-flex justify-content-between"
                          style={{ maxWidth: "300px" }}
                        >
                          <h6>Account Name:</h6>
                          <h6>I-Wheels</h6>
                        </div>
                        <div
                          className="d-flex justify-content-between"
                          style={{ maxWidth: "300px" }}
                        >
                          <h6>Mobile Number:</h6>
                          <h6>0916 509 1083</h6>
                        </div>
                      </div>
                      <p style={{ color: "red", paddingTop: "20px" }}>
                        <b>Note:</b> Please send proof of payment (screenshot,
                        printscreen) to{" "}
                        <span style={{ color: "#33bd33" }}>
                          i-Wheels26@gmail.com
                        </span>{" "}
                        or send a message to our{" "}
                        <a
                          className="d-inline"
                          href="https://www.facebook.com/i-Wheelsph-127061742122440/"
                        >
                          Facebook Page
                        </a>
                        .
                      </p>
                      <br />
                      {!isLoading ? (
                        <button
                          onClick={() => handleSubmit("Paymaya")}
                          className="btn btn-light mb-3"
                        >
                          Confirm Order
                        </button>
                      ) : (
                        <div className="lds-ring">
                          <div></div>
                          <div></div>
                          <div></div>
                          <div></div>
                        </div>
                      )}
                      <hr />
                    </>
                  ) : null}
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PaymentMethods;
