import React, { Component } from "react";
import Link from "next/link";
import dynamic from "next/dynamic";
const OwlCarousel = dynamic(import("react-owl-carousel3"));

const options = {
  loop: true,
  nav: false,
  dots: false,
  autoplayHoverPause: true,
  autoplay: true,
  animateOut: "slideOutDown",
  animateIn: "flipInX",
  items: 1,
  navText: [
    "<i class='fas fa-chevron-left'></i>",
    "<i class='fas fa-chevron-right'></i>",
  ],
};

class TopPanel extends Component {
  state = {
    display: false,
    panel: true,
  };

  componentDidMount() {
    this.setState({ display: true });
  }

  render() {
    let { panel } = this.state;
    return (
      <div className={`top-panel ${panel ? "" : "hide"}`}>
        <div className="container">
          <div className="panel-content">
            {this.state.display ? (
              <OwlCarousel
                className="top-panel-slides owl-carousel owl-theme"
                {...options}
              >
                <div className="single-item-box">
                  <p>
                    <strong>
                    We offer the best brands of Bikes, E-Scooter, E-Bikes & E-Rides available in the market.
                    </strong>
                  </p>
                </div>

                <div className="single-item-box">
                  <p>
                    <strong>1 - 7 Days Delivery Nationwide</strong>
                  </p>
                </div>

                <div className="single-item-box">
                  <p>
                    <strong>Newly Arrived Products!</strong>
                  </p>
                </div>

                {/* <div className="single-item-box">
                                <p><strong>Enjoy an extra 20% off</strong> select sales styles <Link href="#"><a>Read More</a></Link></p>
                            </div>

                            <div className="single-item-box">
                                <p><strong>Enjoy an extra 20% off</strong> select sales styles <Link href="#"><a>Read More</a></Link></p>
                            </div> */}
              </OwlCarousel>
            ) : (
              ""
            )}

            <i
              onClick={() => this.setState({ panel: false })}
              className="fas fa-times panel-close-btn"
            ></i>
          </div>
        </div>
      </div>
    );
  }
}

export default TopPanel;
